import styled from 'styled-components';

export const ProfileContainer = styled.section`
  /* width: 1536px;
  box-sizing: border-box; */
`;

export const ProfileHeader = styled.div`
  width: 1453px;
  margin: 0 auto;

  .profile-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    background: #fff;
    width: 1280px;
    box-shadow: 0px 16px 32px rgba(221, 230, 237, 0.4);
    border-radius: 5px;
    padding: 44px;
    margin: 0 auto;
    /* inage and name */
    &__left {
      display: flex;
      background: red;
    }

    /* image and button */

    &__right {
      .btn-primary {
        font-size: 16px;
        line-height: 24px;
        /* or 150% */

        /* neutral-light-100 */
        background: #3861fb;
        /* neutral-dark-100 */

        border: 1px solid #c7c8d9;
        box-sizing: border-box;
        border-radius: 5px;
        color: #ffffff;
      }
    }
  }
`;
