import React, { useState, useEffect } from 'react';
import {
  GeistUIThemes,
  Button,
  Text,
  Snippet,
  Card,
  Dot,
  Tag,
  User,
  Input,
} from '@geist-ui/react';
import AddressModal from './AddressModal';
import makeStyles from '../makeStyles';
import * as Icons from 'react-feather';
import { definitions } from '../../utils/config.json';

const useStyles = makeStyles((ui) => ({
  card: {
    padding: '0 !important',
    marginBottom: `calc(${ui.layout.gap}*1.5) !important`,
    width: '100%',
  },
  title: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    marginBottom: ui.layout.gap,
    '& h3': {
      margin: 0,
    },
  },
  created: {
    fontSize: 14,
    color: 'rgb(153, 153, 153) !important',
    margin: `0 0 0 ${ui.layout.gapHalf}`,
    textAlign: 'right',
  },
  visitButton: {},
  '@media screen and (max-width: 540px)': {
    created: {
      display: 'none !important',
    },
    visitButton: {
      display: 'none !important',
    },
  },
  dot: {
    display: 'flex !important',
    marginTop: ui.layout.gapQuarter,
    overflow: 'hidden',
    alignItems: 'center !important',
    '& .icon': {
      backgroundColor: '#50e3c2 !important',
    },
    '& .label': {
      textTransform: 'none !important',
      display: 'flex',
      flex: 1,
      overflow: 'hidden',
    },
    '& .label a': {
      display: 'inline-block',
      overflow: 'hidden',
      whiteSpace: 'nowrap',
      textOverflow: 'ellipsis',
      fontSize: 14,
      lineHeight: 'normal',
    },
    '& .link': {
      fontWeight: 500,
    },
  },
  tag: {
    display: 'flex !important',
    alignItems: 'center',
    textTransform: 'capitalize !important',
    fontSize: '12px !important',
    padding: '3px 7px !important',
    borderRadius: '16px !important',
    height: 'unset !important',
    marginLeft: 8,
    color: `${ui.palette.foreground} !important`,
  },
  footer: {
    display: 'flex !important',
    alignItems: 'center !important',
    height: 50,
    width: '100% !important',
    justifyContent: 'flex-end',
  },
  addressButton: {
    display: 'flex !important',
    alignItems: 'center !important',
  },
  repo: {
    fontSize: 14,
    fontWeight: 500,
    marginLeft: '6px !important',
  },
  avatar: {
    width: '32px !important',
    height: '32px !important',
    marginRight: '10px !important',
  },
}));

const ProfileCard = ({ created, repo, icon, address, name, addAddress }) => {
  const [modal, setModal] = useState(false);
  const classes = useStyles();

  return (
    <>
      {/* <AddressModal modal={modal} setModal={setModal} addAddress={addAddress} /> */}

      <Card shadow className={classes.card}>
        <div className={classes.dot}>
          <img
            className={classes.avatar}
            src={`/assets/${name}.svg`}
            alt=''
            srcset=''
          />
          <Snippet text={address} width='auto' />
          <Tag style={{ marginLeft: '8px' }}>{name}</Tag>
        </div>
      </Card>
    </>
  );
};

export default ProfileCard;
