import styled from 'styled-components';

export const ProfileHeader = styled.div`
  align-items: center;
  flex-direction: row;
  justify-content: center;
  margin: 0 auto;
  flex-wrap: wrap;
  background: #fff;
  box-shadow: 0px 16px 32px rgba(221, 230, 237, 0.4);
  width: 1280px;
  padding: 44px;
  .profile-header {
    display: flex;

    justify-content: space-between;

    border-radius: 5px;

    /* inage and name */
    &__left {
      .profile-header__meta {
        display: flex;

        &-name {
          margin: 6px 0 0 22px;
        }
      }
    }

    /* image and button */

    &__right {
      .btn-primary {
        font-size: 16px;
        line-height: 24px;
        /* or 150% */

        /* neutral-light-100 */
        background: #3861fb;
        /* neutral-dark-100 */

        border: 1px solid #c7c8d9;
        box-sizing: border-box;
        border-radius: 5px;
        color: #ffffff;
      }
    }
  }
`;

export const IconContainer = styled.div`
  width: 1280px;
  margin: 40px auto 0 auto;
`;

export const Form = styled.div`
  width: 1280px;
  margin: 24px auto;

  .form-group {
    background: red;
    box-shadow: 0px 16px 32px rgba(221, 230, 237, 0.4);
    border-radius: 5px;
    margin: 22px 0;
    padding: 44px;

    &__input {
      .input {
        border-radius: 5px;
        width: 350px;
        height: 50px;
        margin-right: 60px;
      }
      .input::-webkit-input-placeholder {
        /* Chrome/Opera/Safari */
        color: grey;
      }
    }
  }
`;
